import React, { useState } from 'react';
import '../../styles/tools/PostDownloader.css';

const PostDownloader = () => {
  const [url, setUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const platforms = [
    { name: 'Instagram', icon: '📸' },
    { name: 'TikTok', icon: '🎵' },
    { name: 'Twitter', icon: '🐦' },
    { name: 'Facebook', icon: '👥' },
    { name: 'YouTube', icon: '🎥' },
    { name: 'LinkedIn', icon: '💼' }
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // API call will go here
      console.log('Downloading from:', url);
      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 1500));
    } catch (err) {
      setError('Failed to download content. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="post-downloader">
      <div className="tool-header">
        <h1>Post Downloader</h1>
        <p>Download posts, videos, and images from any social media platform</p>
      </div>

      <div className="download-container">
        <div className="platforms-grid">
          {platforms.map((platform) => (
            <div key={platform.name} className="platform-card">
              <span className="platform-icon">{platform.icon}</span>
              <span className="platform-name">{platform.name}</span>
            </div>
          ))}
        </div>

        <form onSubmit={handleSubmit} className="download-form">
          <div className="input-group">
            <input
              type="url"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              placeholder="Paste your link here..."
              required
              className="url-input"
            />
            <button 
              type="submit" 
              className="download-button"
              disabled={loading}
            >
              {loading ? 'Downloading...' : 'Download'}
            </button>
          </div>
          {error && <div className="error-message">{error}</div>}
        </form>

        <div className="features-list">
          <div className="feature">
            <span className="feature-icon">⚡</span>
            <div className="feature-text">
              <h3>Lightning Fast</h3>
              <p>Download content in seconds</p>
            </div>
          </div>
          <div className="feature">
            <span className="feature-icon">🔒</span>
            <div className="feature-text">
              <h3>Secure Downloads</h3>
              <p>Safe and private downloading</p>
            </div>
          </div>
          <div className="feature">
            <span className="feature-icon">✨</span>
            <div className="feature-text">
              <h3>High Quality</h3>
              <p>Get the best available quality</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PostDownloader;
