import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [isToolsOpen, setIsToolsOpen] = useState(false);

  const tools = {
    'Content Management': [
      { name: 'Post Scheduler', path: '/tools/scheduler' },
      { name: 'Content Calendar', path: '/tools/calendar' },
      { name: 'Bulk Upload', path: '/tools/bulk-upload' },
      { name: 'Auto-posting', path: '/tools/auto-post' }
    ],
    'Analytics & Insights': [
      { name: 'Performance Analytics', path: '/tools/analytics' },
      { name: 'Audience Insights', path: '/tools/audience' },
      { name: 'Competitor Analysis', path: '/tools/competitors' },
      { name: 'ROI Tracking', path: '/tools/roi' }
    ],
    'Content Creation': [
      { name: 'AI Caption Generator', path: '/tools/caption-generator' },
      { name: 'Hashtag Optimizer', path: '/tools/hashtags' },
      { name: 'Image Editor', path: '/tools/image-editor' },
      { name: 'Video Editor', path: '/tools/video-editor' }
    ],
    'Media Management': [
      { name: 'Media Downloader', path: '/tools/downloader' },
      { name: 'Asset Library', path: '/tools/assets' },
      { name: 'Batch Processing', path: '/tools/batch' },
      { name: 'Format Converter', path: '/tools/converter' }
    ],
    'Engagement Tools': [
      { name: 'Comment Manager', path: '/tools/comments' },
      { name: 'DM Automation', path: '/tools/messages' },
      { name: 'Follower Growth', path: '/tools/growth' },
      { name: 'Community Manager', path: '/tools/community' }
    ],
    'Research & Discovery': [
      { name: 'Trend Analysis', path: '/tools/trends' },
      { name: 'Content Research', path: '/tools/research' },
      { name: 'Viral Post Finder', path: '/tools/viral' },
      { name: 'Influencer Discovery', path: '/tools/influencers' }
    ]
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="nav-group">
          <Link to="/" className="nav-link brand">SocialPro</Link>
          <div className="dropdown-wrapper"
               onMouseEnter={() => setIsToolsOpen(true)}
               onMouseLeave={() => setIsToolsOpen(false)}>
            <span className="nav-link">Tools</span>
            {isToolsOpen && (
              <div className="dropdown-menu">
                {Object.entries(tools).map(([category, items]) => (
                  <div key={category} className="dropdown-category">
                    <h3>{category}</h3>
                    <div className="dropdown-items">
                      {items.map((tool) => (
                        <Link 
                          key={tool.path} 
                          to={tool.path}
                          className="dropdown-item"
                        >
                          {tool.name}
                        </Link>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
          <Link to="/pricing" className="nav-link">Pricing</Link>
          <Link to="/about" className="nav-link">About</Link>
        </div>
        <div className="nav-group">
          <Link to="/auth" className="nav-link">Login</Link>
          <Link to="/auth/register" className="nav-button">Sign Up</Link>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
